export const ModeInfos = [
  "LOCKING mode preserves the original token supply on a single chain",
  "BURNING mode deploy natively multichain tokens with the supply distributed among multiple chains"
]

export function getModeInfo(mode) {
  if (mode == "LOCKING") {
    return ModeInfos[0];
  } else if (mode == "BURNING") {
    return ModeInfos[1];
  }
}

export function toModeId(mode) {
  if (mode == "LOCKING") {
    return 0;
  } else if (mode == "BURNING") {
    return 1;
  }

  return 0;
}

export function getIcon(chain) {
  if (typeof chain == "number") {
    chain = toChain(chain as number);
  }
  if (chain == "Sui") {
    return Icon.SUI;
  } else if (chain == "Aptos") {
    return Icon.APT;
  }
}